import React from 'react';
import ReactDOM from 'react-dom';
import { Tabs } from 'antd';
import Menulist from './Menulist';

import 'antd/dist/antd.css';
import '../css/menutab.css';


const { TabPane } = Tabs;

function callback(key) {
    console.log(key);
}


class menutap extends React.Component{

    constructor(props) {
        super(props);

        this.state = {
            name : '',
            coffee : [
                {id : 0, name : '아메리카노', price : 1000, qty : 99, img : 'http://ldb.phinf.naver.net/20190214_188/1550152911809qiiPP_JPEG/jpi5HJk-J6pDhBl4-s92vz6g.JPG.jpg'},
                {id : 1, name : '에스프레소', price : 1000, qty : 99, img : 'http://blogfiles.naver.net/20140804_179/baristadic_1407133864241k1oH4_JPEG/%BF%A1%BD%BA%C7%C1%B7%B9%BC%D2_2.jpg'},
                {id : 2, name : '카페라떼', price : 1500, qty : 99, img : 'https://t1.daumcdn.net/cfile/tistory/9931783C5BAC52F329'},
                {id : 3, name : '카푸치노', price : 2500, qty : 99, img : 'http://imgnews.naver.net/image/030/2015/04/21/article_21101925729403_99_20150421102107.jpg'},
                {id : 4, name : '카페모카', price : 2000, qty : 99, img : 'http://blogfiles.naver.net/20111130_180/cafeprblog_1322625767817MufGQ_JPEG/%C4%AB%C6%E4%B8%F0%C4%AB4.jpg'},
            ],
            drink : [
                {id : 0, name : '사이다', price : 1000, qty : 99, img : 'http://image.auction.co.kr/itemimage/16/5c/ba/165cba7b21.jpg'},
                {id : 1, name : '콜라', price : 1000, qty : 99, img : 'http://image.auction.co.kr/itemimage/10/69/c3/1069c3a0e6.jpg'},
                {id : 2, name : '환타', price : 1000, qty : 99, img : 'http://image.auction.co.kr/itemimage/13/30/aa/1330aa7321.jpg'},
                {id : 3, name : '밀키스', price : 1400, qty : 99, img : 'http://image.lottemart.com/lim/static_root/images/prodimg/88010/8801056043551_1_640.jpg'},
                {id : 4, name : '핫식스', price : 1500, qty : 99, img : 'data:image/jpeg;base64,'},
                {id : 5, name : '코코팜', price : 1200, qty : 99, img : 'data:image/jpeg;base64,'},
            ],
            snack : [
                {id : 0, name : '팝콘', price : 2000, qty : 99, img : 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTvDWmq2a8ygx89Lm_l5xyDjBCcll-RSmoq6JV7QmDmU0adIVg4'},
                {id : 1, name : '고래밥', price : 1500, qty : 99, img : 'http://www.thessan.com/shopimages/thessancom/0060020003762.jpg?1510540931'},
                {id : 2, name : '빼빼로', price : 1000, qty : 99, img : 'http://img.danawa.com/prod_img/500000/371/255/img/2255371_1.jpg?shrink=500:500&_v=20171107173806'},
            ],
            basket : []
        }
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        const {handleInit} = this.props;
        console.log(this.state.basket);

        //state 값과 이전에 받아온 state 값이 다를때만 실행
        if (prevState.basket !== this.state.basket) {
            handleInit(this.state.basket);
        }
    }


    handleClick = (coffee) => {
        this.setState({
            basket : this.state.basket.concat({id: coffee.id, name: coffee.name, price: coffee.price, qty: coffee.qty, img: coffee.img})
        });
    }

    render(){
        const { coffee, drink, snack } = this.state;
        let i = 0;

        return(
            <div>
                <Tabs defaultActiveKey="1" onChange={callback}>
                    <TabPane tab="커피" key="1">
                        <div className='list-container'>
                            {
                                coffee.map(coffee => (
                                    <div key = {i++} onClick={this.handleClick.bind(this, coffee)}>
                                        <Menulist
                                            key={coffee.id}
                                            id={coffee.id}
                                            name={coffee.name}
                                            price={coffee.price}
                                            qty={coffee.qty}
                                            img={coffee.img}
                                        />
                                    </div>
                                    )
                                )
                            }
                        </div>
                    </TabPane>
                    <TabPane tab="음료" key="2">
                        <div className='list-container'>
                            {
                                drink.map(drink => (
                                        <Menulist
                                            key={drink.id}
                                            id={drink.id}
                                            name={drink.name}
                                            price={drink.price}
                                            qty={drink.qty}
                                            img={drink.img}
                                        />
                                    )
                                )
                            }
                        </div>
                    </TabPane>
                    <TabPane tab="스낵" key="3">
                        <div className='list-container'>
                            {
                                snack.map(snack => (
                                        <Menulist
                                            key={snack.id}
                                            id={snack.id}
                                            name={snack.name}
                                            price={snack.price}
                                            qty={snack.qty}
                                            img={snack.img}
                                        />
                                    )
                                )
                            }
                        </div>
                    </TabPane>
                </Tabs>
            </div>
        );
    }
}


export default menutap;
